content.text:
The node accepts these properties:
<node_properties_definition>
[
  {
    "displayName": "Authentication",
    "name": "authentication",
    "type": "options",
    "options": [
      {
        "name": "OAuth2 (recommended)",
        "value": "oAuth2"
      },
      {
        "name": "Service Account",
        "value": "serviceAccount"
      }
    ],
    "default": "oAuth2"
  },
  {
    "displayName": "Resource",
    "name": "resource",
    "type": "options",
    "noDataExpression": true,
    "options": [
      {
        "name": "Message",
        "value": "message"
      },
      {
        "name": "Label",
        "value": "label"
      },
      {
        "name": "Draft",
        "value": "draft"
      },
      {
        "name": "Thread",
        "value": "thread"
      }
    ],
    "default": "message"
  },
  {
    "displayName": "Operation",
    "name": "operation",
    "type": "options",
    "noDataExpression": true,
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ]
      }
    },
    "options": [
      {
        "name": "Add Label",
        "value": "addLabels",
        "action": "Add label to message"
      },
      {
        "name": "Delete",
        "value": "delete",
        "action": "Delete a message"
      },
      {
        "name": "Get",
        "value": "get",
        "action": "Get a message"
      },
      {
        "name": "Get Many",
        "value": "getAll",
        "action": "Get many messages"
      },
      {
        "name": "Mark as Read",
        "value": "markAsRead",
        "action": "Mark a message as read"
      },
      {
        "name": "Mark as Unread",
        "value": "markAsUnread",
        "action": "Mark a message as unread"
      },
      {
        "name": "Remove Label",
        "value": "removeLabels",
        "action": "Remove label from message"
      },
      {
        "name": "Reply",
        "value": "reply",
        "action": "Reply to a message"
      },
      {
        "name": "Send",
        "value": "send",
        "action": "Send a message"
      },
      {
        "name": "Send and Wait for Response",
        "value": "sendAndWait",
        "action": "Send message and wait for response"
      }
    ],
    "default": "send"
  },
  {
    "displayName": "Return All",
    "name": "returnAll",
    "type": "boolean",
    "displayOptions": {
      "show": {
        "operation": [
          "getAll"
        ],
        "resource": [
          "message"
        ]
      }
    },
    "default": false,
    "description": "Whether to return all results or only up to a given limit"
  },
  {
    "displayName": "Limit",
    "name": "limit",
    "type": "number",
    "displayOptions": {
      "show": {
        "operation": [
          "getAll"
        ],
        "resource": [
          "message"
        ],
        "returnAll": [
          false
        ]
      }
    },
    "typeOptions": {
      "minValue": 1,
      "maxValue": 500
    },
    "default": 50,
    "description": "Max number of results to return"
  },
  {
    "displayName": "Simplify",
    "name": "simple",
    "type": "boolean",
    "displayOptions": {
      "show": {
        "operation": [
          "getAll"
        ],
        "resource": [
          "message"
        ]
      }
    },
    "default": true,
    "description": "Whether to return a simplified version of the response instead of the raw data"
  },
  {
    "displayName": "Fetching a lot of messages may take a long time. Consider using filters to speed things up",
    "name": "filtersNotice",
    "type": "notice",
    "default": "",
    "displayOptions": {
      "show": {
        "operation": [
          "getAll"
        ],
        "resource": [
          "message"
        ],
        "returnAll": [
          true
        ]
      }
    }
  },
  {
    "displayName": "Filters",
    "name": "filters",
    "type": "collection",
    "placeholder": "Add Filter",
    "default": {},
    "displayOptions": {
      "show": {
        "operation": [
          "getAll"
        ],
        "resource": [
          "message"
        ]
      }
    },
    "options": [
      {
        "displayName": "Include Spam and Trash",
        "name": "includeSpamTrash",
        "type": "boolean",
        "default": false,
        "description": "Whether to include messages from SPAM and TRASH in the results"
      },
      {
        "displayName": "Label Names or IDs",
        "name": "labelIds",
        "type": "multiOptions",
        "typeOptions": {
          "loadOptionsMethod": "getLabels"
        },
        "default": [],
        "description": "Only return messages with labels that match all of the specified label IDs. Choose from the list, or specify IDs using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>."
      },
      {
        "displayName": "Search",
        "name": "q",
        "type": "string",
        "default": "",
        "placeholder": "has:attachment",
        "hint": "Use the same format as in the Gmail search box. <a href=\"https://support.google.com/mail/answer/7190?hl=en\">More info</a>.",
        "description": "Only return messages matching the specified query"
      },
      {
        "displayName": "Read Status",
        "name": "readStatus",
        "type": "options",
        "default": "unread",
        "hint": "Filter emails by whether they have been read or not",
        "options": [
          {
            "name": "Unread and read emails",
            "value": "both"
          },
          {
            "name": "Unread emails only",
            "value": "unread"
          },
          {
            "name": "Read emails only",
            "value": "read"
          }
        ]
      },
      {
        "displayName": "Received After",
        "name": "receivedAfter",
        "type": "dateTime",
        "default": "",
        "description": "Get all emails received after the specified date. In an expression you can set date using string in ISO format or a timestamp in miliseconds."
      },
      {
        "displayName": "Received Before",
        "name": "receivedBefore",
        "type": "dateTime",
        "default": "",
        "description": "Get all emails received before the specified date. In an expression you can set date using string in ISO format or a timestamp in miliseconds."
      },
      {
        "displayName": "Sender",
        "name": "sender",
        "type": "string",
        "default": "",
        "description": "Sender name or email to filter by",
        "hint": "Enter an email or part of a sender name"
      }
    ]
  },
  {
    "displayName": "Options",
    "name": "options",
    "type": "collection",
    "placeholder": "Add option",
    "default": {},
    "displayOptions": {
      "show": {
        "operation": [
          "getAll"
        ],
        "resource": [
          "message"
        ]
      },
      "hide": {
        "simple": [
          true
        ]
      }
    },
    "options": [
      {
        "displayName": "Attachment Prefix",
        "name": "dataPropertyAttachmentsPrefixName",
        "type": "string",
        "default": "attachment_",
        "description": "Prefix for name of the binary property to which to write the attachment. An index starting with 0 will be added. So if name is 'attachment_' the first attachment is saved to 'attachment_0'."
      },
      {
        "displayName": "Download Attachments",
        "name": "downloadAttachments",
        "type": "boolean",
        "default": false,
        "description": "Whether the email's attachments will be downloaded and included in the output"
      }
    ]
  }
]
</node_properties_definition>
