content.text:
The node accepts these properties:
<node_properties_definition>
[
  {
    "displayName": "Authentication",
    "name": "authentication",
    "type": "options",
    "options": [
      {
        "name": "OAuth2 (recommended)",
        "value": "oAuth2"
      },
      {
        "name": "Service Account",
        "value": "serviceAccount"
      }
    ],
    "default": "oAuth2"
  },
  {
    "displayName": "Resource",
    "name": "resource",
    "type": "options",
    "noDataExpression": true,
    "options": [
      {
        "name": "Message",
        "value": "message"
      },
      {
        "name": "Label",
        "value": "label"
      },
      {
        "name": "Draft",
        "value": "draft"
      },
      {
        "name": "Thread",
        "value": "thread"
      }
    ],
    "default": "message"
  },
  {
    "displayName": "Operation",
    "name": "operation",
    "type": "options",
    "noDataExpression": true,
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ]
      }
    },
    "options": [
      {
        "name": "Add Label",
        "value": "addLabels",
        "action": "Add label to message"
      },
      {
        "name": "Delete",
        "value": "delete",
        "action": "Delete a message"
      },
      {
        "name": "Get",
        "value": "get",
        "action": "Get a message"
      },
      {
        "name": "Get Many",
        "value": "getAll",
        "action": "Get many messages"
      },
      {
        "name": "Mark as Read",
        "value": "markAsRead",
        "action": "Mark a message as read"
      },
      {
        "name": "Mark as Unread",
        "value": "markAsUnread",
        "action": "Mark a message as unread"
      },
      {
        "name": "Remove Label",
        "value": "removeLabels",
        "action": "Remove label from message"
      },
      {
        "name": "Reply",
        "value": "reply",
        "action": "Reply to a message"
      },
      {
        "name": "Send",
        "value": "send",
        "action": "Send a message"
      },
      {
        "name": "Send and Wait for Response",
        "value": "sendAndWait",
        "action": "Send message and wait for response"
      }
    ],
    "default": "send"
  },
  {
    "displayName": "To",
    "name": "sendTo",
    "type": "string",
    "default": "",
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ],
        "operation": [
          "send"
        ]
      }
    },
    "placeholder": "info@example.com",
    "description": "The email addresses of the recipients. Multiple addresses can be separated by a comma. e.g. jay@getsby.com, jon@smith.com."
  },
  {
    "displayName": "Subject",
    "name": "subject",
    "type": "string",
    "default": "",
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ],
        "operation": [
          "send"
        ]
      }
    },
    "placeholder": "Hello World!"
  },
  {
    "displayName": "Email Type",
    "name": "emailType",
    "type": "options",
    "default": "html",
    "required": true,
    "noDataExpression": true,
    "options": [
      {
        "name": "Text",
        "value": "text"
      },
      {
        "name": "HTML",
        "value": "html"
      }
    ],
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ],
        "operation": [
          "send",
          "reply"
        ]
      },
      "hide": {
        "@version": [
          2
        ]
      }
    }
  },
  {
    "displayName": "Message",
    "name": "message",
    "type": "string",
    "default": "",
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ],
        "operation": [
          "reply",
          "send"
        ]
      }
    }
  },
  {
    "displayName": "Options",
    "name": "options",
    "type": "collection",
    "placeholder": "Add option",
    "displayOptions": {
      "show": {
        "resource": [
          "message"
        ],
        "operation": [
          "send",
          "reply"
        ]
      }
    },
    "default": {},
    "options": [
      {
        "displayName": "Append n8n Attribution",
        "name": "appendAttribution",
        "type": "boolean",
        "default": true,
        "description": "Whether to include the phrase ‚ÄúThis email was sent automatically with n8n‚Äù to the end of the email"
      },
      {
        "displayName": "Attachments",
        "name": "attachmentsUi",
        "placeholder": "Add Attachment",
        "type": "fixedCollection",
        "typeOptions": {
          "multipleValues": true
        },
        "options": [
          {
            "name": "attachmentsBinary",
            "displayName": "Attachment Binary",
            "values": [
              {
                "displayName": "Attachment Field Name",
                "name": "property",
                "type": "string",
                "default": "data",
                "description": "Add the field name from the input node. Multiple properties can be set separated by comma.",
                "hint": "The name of the field with the attachment in the node input"
              }
            ]
          }
        ],
        "default": {},
        "description": "Array of supported attachments to add to the message"
      },
      {
        "displayName": "BCC",
        "name": "bccList",
        "type": "string",
        "description": "The email addresses of the blind copy recipients. Multiple addresses can be separated by a comma. e.g. jay@getsby.com, jon@smith.com.",
        "placeholder": "info@example.com",
        "default": ""
      },
      {
        "displayName": "CC",
        "name": "ccList",
        "type": "string",
        "description": "The email addresses of the copy recipients. Multiple addresses can be separated by a comma. e.g. jay@getsby.com, jon@smith.com.",
        "placeholder": "info@example.com",
        "default": ""
      },
      {
        "displayName": "Sender Name",
        "name": "senderName",
        "type": "string",
        "placeholder": "e.g. Nathan",
        "default": "",
        "description": "The name that will be shown in recipients' inboxes"
      },
      {
        "displayName": "Send Replies To",
        "name": "replyTo",
        "type": "string",
        "placeholder": "reply@example.com",
        "default": "",
        "description": "The email address that the reply message is sent to",
        "displayOptions": {
          "hide": {
            "/operation": [
              "reply"
            ]
          }
        }
      },
      {
        "displayName": "Reply to Sender Only",
        "name": "replyToSenderOnly",
        "type": "boolean",
        "default": false,
        "description": "Whether to reply to the sender only or to the entire list of recipients"
      }
    ]
  }
]
</node_properties_definition>
