content.text:
The node accepts these properties:
<node_properties_definition>
[
  {
    "displayName": "Resource",
    "name": "resource",
    "type": "options",
    "noDataExpression": true,
    "options": [
      {
        "name": "Text",
        "value": "text"
      },
      {
        "name": "Image",
        "value": "image"
      },
      {
        "name": "Audio",
        "value": "audio"
      },
      {
        "name": "File",
        "value": "file"
      },
      {
        "name": "Conversation",
        "value": "conversation"
      },
      {
        "name": "Video",
        "value": "video"
      }
    ],
    "default": "text"
  },
  {
    "displayName": "Operation",
    "name": "operation",
    "type": "options",
    "noDataExpression": true,
    "options": [
      {
        "name": "Analyze Image",
        "value": "analyze",
        "action": "Analyze image",
        "description": "Take in images and answer questions about them"
      },
      {
        "name": "Generate an Image",
        "value": "generate",
        "action": "Generate an image",
        "description": "Creates an image from a text prompt"
      },
      {
        "name": "Edit Image",
        "value": "edit",
        "action": "Edit image",
        "description": "Edit an image"
      }
    ],
    "default": "generate",
    "displayOptions": {
      "show": {
        "resource": [
          "image"
        ]
      }
    }
  },
  {
    "displayName": "Model",
    "name": "model",
    "type": "options",
    "default": "dall-e-3",
    "description": "The model to use for image generation",
    "options": [
      {
        "name": "DALL¬∑E 2",
        "value": "dall-e-2"
      },
      {
        "name": "DALL¬∑E 3",
        "value": "dall-e-3"
      },
      {
        "name": "GPT Image 1",
        "value": "gpt-image-1"
      }
    ],
    "displayOptions": {
      "show": {
        "operation": [
          "generate"
        ],
        "resource": [
          "image"
        ]
      }
    }
  },
  {
    "displayName": "Prompt",
    "name": "prompt",
    "type": "string",
    "placeholder": "e.g. A cute cat eating a dinosaur",
    "description": "A text description of the desired image(s). The maximum length is 1000 characters for dall-e-2 and 4000 characters for dall-e-3.",
    "default": "",
    "typeOptions": {
      "rows": 2
    },
    "displayOptions": {
      "show": {
        "operation": [
          "generate"
        ],
        "resource": [
          "image"
        ]
      }
    }
  },
  {
    "displayName": "Options",
    "name": "options",
    "placeholder": "Add Option",
    "type": "collection",
    "default": {},
    "options": [
      {
        "displayName": "Number of Images",
        "name": "n",
        "default": 1,
        "description": "Number of images to generate",
        "type": "number",
        "typeOptions": {
          "minValue": 1,
          "maxValue": 10
        },
        "displayOptions": {
          "show": {
            "/model": [
              "dall-e-2"
            ]
          }
        }
      },
      {
        "displayName": "Quality",
        "name": "dalleQuality",
        "type": "options",
        "description": "The quality of the image that will be generated, HD creates images with finer details and greater consistency across the image",
        "options": [
          {
            "name": "HD",
            "value": "hd"
          },
          {
            "name": "Standard",
            "value": "standard"
          }
        ],
        "displayOptions": {
          "show": {
            "/model": [
              "dall-e-3"
            ]
          }
        },
        "default": "standard"
      },
      {
        "displayName": "Quality",
        "name": "quality",
        "type": "options",
        "description": "The quality of the image that will be generated, High creates images with finer details and greater consistency across the image",
        "options": [
          {
            "name": "High",
            "value": "high"
          },
          {
            "name": "Medium",
            "value": "medium"
          },
          {
            "name": "Low",
            "value": "low"
          }
        ],
        "displayOptions": {
          "show": {
            "/model": [
              "gpt-image-1"
            ]
          }
        },
        "default": "medium"
      },
      {
        "displayName": "Resolution",
        "name": "size",
        "type": "options",
        "options": [
          {
            "name": "256x256",
            "value": "256x256"
          },
          {
            "name": "512x512",
            "value": "512x512"
          },
          {
            "name": "1024x1024",
            "value": "1024x1024"
          }
        ],
        "displayOptions": {
          "show": {
            "/model": [
              "dall-e-2"
            ]
          }
        },
        "default": "1024x1024"
      },
      {
        "displayName": "Resolution",
        "name": "size",
        "type": "options",
        "options": [
          {
            "name": "1024x1024",
            "value": "1024x1024"
          },
          {
            "name": "1792x1024",
            "value": "1792x1024"
          },
          {
            "name": "1024x1792",
            "value": "1024x1792"
          }
        ],
        "displayOptions": {
          "show": {
            "/model": [
              "dall-e-3"
            ]
          }
        },
        "default": "1024x1024"
      },
      {
        "displayName": "Resolution",
        "name": "size",
        "type": "options",
        "options": [
          {
            "name": "1024x1024",
            "value": "1024x1024"
          },
          {
            "name": "1024x1536",
            "value": "1024x1536"
          },
          {
            "name": "1536x1024",
            "value": "1536x1024"
          }
        ],
        "displayOptions": {
          "show": {
            "/model": [
              "gpt-image-1"
            ]
          }
        },
        "default": "1024x1024"
      },
      {
        "displayName": "Style",
        "name": "style",
        "type": "options",
        "options": [
          {
            "name": "Natural",
            "value": "natural",
            "description": "Produce more natural looking images"
          },
          {
            "name": "Vivid",
            "value": "vivid",
            "description": "Lean towards generating hyper-real and dramatic images"
          }
        ],
        "displayOptions": {
          "show": {
            "/model": [
              "dall-e-3"
            ]
          }
        },
        "default": "vivid"
      },
      {
        "displayName": "Respond with Image URL(s)",
        "name": "returnImageUrls",
        "type": "boolean",
        "default": false,
        "description": "Whether to return image URL(s) instead of binary file(s)",
        "displayOptions": {
          "hide": {
            "/model": [
              "gpt-image-1"
            ]
          }
        }
      },
      {
        "displayName": "Put Output in Field",
        "name": "binaryPropertyOutput",
        "type": "string",
        "default": "data",
        "hint": "The name of the output field to put the binary file data in",
        "displayOptions": {
          "show": {
            "returnImageUrls": [
              false
            ]
          }
        }
      }
    ],
    "displayOptions": {
      "show": {
        "operation": [
          "generate"
        ],
        "resource": [
          "image"
        ]
      }
    }
  }
]
</node_properties_definition>
